package day03;

public class Checklanguage {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String lang="Java";
		if(lang=="Python") {
			System.out.println("The language is Python");
		}
		else if(lang=="C++") {
			System.out.println("The language is C++");
		}
		else if(lang=="Java") {
			System.out.println("The language is Java");
		}
		else {
			System.out.println(lang);
		}

	}

}
